import React from 'react'
//import WalletService from '../services/WalletService'
import { useState } from 'react';
import axios from 'axios';

const WALLET_API_BASE_URL = "http://localhost:8080/wallet/";

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    //handle change
    const handleOnChange = (e) => {
        setUsername(e.target.username);
        setPassword(e.target.password);
    };

    const validate = () => {
        if (username.trim().length == 0) return false
        if (password.trim().length == 0) return false
        return true
    }

    const handleRegister = (e) => {
        e.preventDefault(); // avoid page refresh
        let isValid = true;//validate();
        //console.log(isValid);

        if (isValid) {
          //console.log(this.state)
          //WalletService.createWallet(this.state)
          axios.post(WALLET_API_BASE_URL+'/create', [{username:"rushal", password:"pwd"}])
            .then(response => {
                console.log(response);
                alert("Registration successful");
            })
            .catch(error => {
                console.log(error);
            });

            setUsername('');
            setPassword('');
        }
    }

    const handleLogin = (e) => {
        e.preventDefault(); // avoid page refresh
        let isValid = validate();
        //console.log(isValid);

        if (isValid) {
          //console.log(this.state)
          //WalletService.getWallet(this.state)
          axios.post(WALLET_API_BASE_URL+'/get', {username:username, password:password})
            .then(response => {
                console.log(response);
                alert("Login successful");
            })
            .catch(error => {
                console.log(error);
            });

            setUsername("");
            setPassword("");
        }
    }

    return (
        <>
        <h1>Welcome</h1>
        <form onSubmit={handleRegister}>
          <div>
            <input
              name="username"
              placeholder="username"
              value={username}
              onChange={handleOnChange}
            />

          </div>

          <div>
            <input
              name="password"
              placeholder="password"
              value={password}
              onChange={handleOnChange}
            />

          </div>
          <button onClick={handleLogin}>Login</button>
          <button type="submit">Register</button>
        </form>
      </>
    )
}
export default Login;
