import React, {useState} from 'react'
import { useLocation, useNavigate } from 'react-router-dom'

const WALLET_API_BASE_URL = "http://localhost:8080/wallet"

const Wallet = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const username = location.state.username;
    const [amt, setAmt] = useState('');
    var showBalance = false;
    var showHistory = false;

    const validate = () => {
        if (amt.trim().length > 0 && !isNaN(+amt)) {return true}
        return false
    }

    const handleBalance = (e) => {
        e.preventDefault();
        showBalance = true;
        showHistory = false;
    }

    const handleAdd = (e) => {
        e.preventDefault();
        let isValid = validate();

        if (isValid) {
        axios.put(WALLET_API_BASE_URL+"/login",
        {
        username: uname,
        password: pwd
        })
            .then(response => {
              //  console.log(response);
                if(response.data.errorCode === 1){
                    alert("Login Failed! Invalid credentials.");
                    setUname('');
                    setPwd('');
                }
                else{
                 //   setAuthenticated(true)
                 //   localStorage.setItem("authenticated", true);
                    alert("Login successful");
                    setUname('');
                    setPwd('');
                    navigate("/wallet", {
                        state:{
                            username: uname
                        }
                    });
                }
            })
            .catch(error => {
                console.log(error);
                alert("Login failed! Error occured");
            });
        }
        else{
            alert("Invalid input")
        }
    }
    const handleWithdraw = (e) => {
        e.preventDefault();
    }
    const handleHistory = (e) => {
        e.preventDefault();
        showBalance = false;
        showHistory = true;
    }

    const handleLogout = (e) => {
        e.preventDefault();
        navigate("/login")
    }



    return (
      <div>
        <h1>Hi {username}! Welcome to your wallet</h1>
        {/* <form onSubmit={handleBalance}> */}
          <div>
            <input
              type="number"
              precision = {2}
              min = {0}
              name="amount"
              placeholder="amount"
              value={amt}
              onChange={(e) =>
                {
                    setAmt(e.target.value);      
                }}
            /> 
          </div>

          <button onClick={handleAdd}>Add</button>
          <button onClick={handleWithdraw}>Withdraw</button>
          <button onClick={handleBalance}>Balance</button>
          {/* <button type="submit">Balance</button> */}
          <button onClick={handleHistory}>History</button>
          <p></p>
            {<p>Balance: </p> && showBalance}
            {<p>History: </p> && showHistory}
        {/* </form> */}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
};
export default Wallet;
