import React, {useState} from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import axios from 'axios'

const Wallet = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const username = location.state.username;
    const [amt, setAmt] = useState('');
    var balance = "";
    var history = "";
    var showBalance = false;
    var showHistory = false;
    const USER_WALLET_API_BASE_URL = "http://localhost:8080/wallet/" + username; 

    const validate = () => {
        if (amt.trim().length > 0 && !isNaN(+amt)) {return true}
        return false
    }

    const handleBalance = (e) => {
        e.preventDefault();
        showBalance = true;
        showHistory = false;
        let isValid = validate();
        if (isValid) {
        axios.put(USER_WALLET_API_BASE_URL+"/balance/")
            .then(response => {
              alert(response.data.message)
              setAmt('');
            })
            .catch(error => {
                console.log(error);
                alert("Error occured");
            });
        }
        else{
            alert("Invalid input")
        }
    }

    const handleAdd = (e) => {
        e.preventDefault();
        let isValid = validate();
        if (isValid) {
        axios.put(USER_WALLET_API_BASE_URL+"/add/" + amt)
            .then(response => {
              alert(response.data.message)
              setAmt('');
            })
            .catch(error => {
                console.log(error);
                alert("Error occured");
            });
        }
        else{
            alert("Invalid input")
        }
    }
    const handleWithdraw = (e) => {
        e.preventDefault();
        let isValid = validate();
        if (isValid) {
        axios.put(USER_WALLET_API_BASE_URL+"/withdraw/" + amt)
            .then(response => {
              alert(response.data.message)
              setAmt('');
            })
            .catch(error => {
                console.log(error);
                alert("Error occured");
            });
        }
        else{
            alert("Invalid input")
        }
    }
    const handleHistory = (e) => {
        e.preventDefault();
        showBalance = false;
        showHistory = true;
    }

    const handleLogout = (e) => {
        e.preventDefault();
        navigate("/login")
    }



    return (
      <div>
        <h1>Hi {username}! Welcome to your wallet</h1>
        {/* <form onSubmit={handleBalance}> */}
          <div>
            <input
              type="number"
              precision = {2}
              min = {0}
              name="amount"
              placeholder="amount"
              value={amt}
              onChange={(e) =>
                {
                    setAmt(e.target.value);      
                }}
            /> 
          </div>

          <button onClick={handleAdd}>Add</button>
          <button onClick={handleWithdraw}>Withdraw</button>
          <button onClick={handleBalance}>Balance</button>
          {/* <button type="submit">Balance</button> */}
          <button onClick={handleHistory}>History</button>
          <p></p>
            {<p>Balance: {balance}</p> && showBalance}
            {<p>History: {history}</p> && showHistory}
        {/* </form> */}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
};
export default Wallet;
