import React, {useState} from 'react'
import { useLocation, useNavigate } from 'react-router-dom'

const Wallet = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const username = location.state.username;
    const [amt, setAmt] = useState('');
    var showBalance = false;
    var showHistory = false;

    const validate = () => {
        if (uname.trim().length > 0 && pwd.trim().length > 0) {return true}
        return false
    }

    const handleBalance = (e) => {
        e.preventDefault();
        showBalance = true;
        showHistory = false;
    }

    const handleAdd = (e) => {
        e.preventDefault();
    }
    const handleWithdraw = (e) => {
        e.preventDefault();
    }
    const handleHistory = (e) => {
        e.preventDefault();
        showBalance = false;
        showHistory = true;
    }

    const handleLogout = (e) => {
        e.preventDefault();
        navigate("/login")
    }



    return (
      <div>
        <h1>Hi {username}! Welcome to your wallet</h1>
        {/* <form onSubmit={handleBalance}> */}
          <div>
            <input
              type="number"
              precision = {2}
              min = {0}
              name="amount"
              placeholder="amount"
              value={amt}
              onChange={(e) =>
                {
                    setAmt(e.target.value);      
                }}
            /> 
          </div>

          <button onClick={handleAdd}>Add</button>
          <button onClick={handleWithdraw}>Withdraw</button>
          <button onClick={handleBalance}>Balance</button>
          {/* <button type="submit">Balance</button> */}
          <button onClick={handleHistory}>History</button>
          <p></p>
          <textarea>
            {<p>Balance: </p> && showBalance}
            {<p>History: </p> && showHistory}
          </textarea>
        {/* </form> */}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
};
export default Wallet;
