import React from 'react'
//import WalletService from '../services/WalletService'
import { useState } from 'react';
import axios from 'axios';

const WALLET_API_BASE_URL = "http://localhost:8080/wallet";

function Login() {
    const [uname, setUname] = useState('');
    const [pwd, setPwd] = useState('');
    const [authenticated, setauthenticated] = useState(localStorage.getItem(localStorage.getItem("authenticated")|| false));

    const validate = () => {
        if (uname.trim().length > 0 && pwd.trim().length > 0) {return true}
        return false
    }

    async function handleRegister(e){
        e.preventDefault();
       let isValid = validate();
    if(isValid)
    {
        try
        {
         await axios.post(WALLET_API_BASE_URL+"/create",
        {
        username: uname,
        password: pwd
        });
          alert("User Registation Successful");
          setUname('');
          setPwd('');
        }
    catch(err)
        {
          alert("User Registation Failed");
        }
    }
    else{
        alert("Invalid input")
    }
    }

    // async function handleLogin(e){
    //     e.preventDefault();
    //    let isValid = validate();
    // if(isValid)
    // {
    //     try
    //     {
    //      await axios.get(WALLET_API_BASE_URL+"/get",
    //     {
    //     username: uname,
    //     password: pwd
    //     });
    //       alert("Login Successful");
    //       setUname('');
    //       setPwd('');
    //     }
    // catch(err)
    //     {
    //       alert("Login Failed");
    //     }
    // }
    // else{
    //     alert("Invalid input")
    // }
    // }

    const handleLogin = (e) => {
        e.preventDefault();
        let isValid = validate();

        if (isValid) {
          axios.get(WALLET_API_BASE_URL+"/get", {params:{
            username: uname, 
            password: pwd
        }})
            .then(response => {
                console.log(response);
                alert("Login successful");
                setUname('');
                setPwd('');
            })
            .catch(error => {
                console.log(error);
                alert("Login failed");
            });
        }
    }

    return (
        <>
        <h1>Welcome</h1>
        <form onSubmit={handleRegister}>
          <div>
            <input
              type="text"
              name="username"
              placeholder="username"
              value={uname}
             onChange={(e) =>
                {
                    setUname(e.target.value);      
                }}
            />

          </div>

          <div>
            <input
              type="text"
              name="password"
              placeholder="password"
              value={pwd}
             onChange={(e) =>
                {
                    setPwd(e.target.value);      
                }}
            />

          </div>
          <button onClick={handleLogin}>Login</button>
          <button type="submit">Register</button>
        </form>
      </>
    )
}
export default Login;
